import React from 'react';
import PropTypes from 'prop-types';
import { List } from 'semantic-ui-react';

/**
 * This object renders  a single review, with accompanying rating, 
 * score, date, etc.
 */
const MatchItem = props => (
  <List.Item>
    <List.Content>
      <List.Header>
        <List.Description>
          <span dangerouslySetInnerHTML={{__html: props.title}}></span>
        </List.Description>
      </List.Header>
    </List.Content>
    <List.Content>
      <List.Description>
        <span dangerouslySetInnerHTML={{__html: props.text}}></span>
        { props.moreButton }
      </List.Description>
    </List.Content>
    <List.Content>
      Score: { props.score }
    </List.Content>
    <List.Content>
      Date: { props.date }
    </List.Content>
    <List.Content>
      Sentiment: { props.sentiment }
    </List.Content>
  </List.Item>
);

// type check to ensure we are called correctly
MatchItem.propTypes = {
  title: PropTypes.string.isRequired,
  text: PropTypes.string.isRequired,
  moreButton: PropTypes.object.isRequired,
  date: PropTypes.string.isRequired,
  score: PropTypes.string.isRequired,
  sentiment: PropTypes.object.isRequired
};

// export so we are visible to parent
module.exports = MatchItem;

